import React, { useState } from 'react';
import Resume from './components/Resume';
import { jsPDF } from 'jspdf';
import html2canvas from 'html2canvas';
import './App.css';

function App() {
  const [isGenerating, setIsGenerating] = useState(false);

  const generatePDF = async () => {
    setIsGenerating(true);

    try {
      const pdf = new jsPDF({
        orientation: 'portrait',
        unit: 'mm',
        format: 'a4',
        compress: true
      });

      // Set up fonts and colors
      pdf.setFont('helvetica');
      const pageWidth = 210;
      const pageHeight = 297;
      const margin = 15;
      const contentWidth = pageWidth - (2 * margin);
      let yPosition = margin;

      // Helper function to add text with word wrap and page break handling
      const addText = (text, fontSize = 10, isBold = false, color = [0, 0, 0]) => {
        pdf.setFontSize(fontSize);
        pdf.setFont('helvetica', isBold ? 'bold' : 'normal');
        pdf.setTextColor(color[0], color[1], color[2]);

        const lines = pdf.splitTextToSize(text, contentWidth);
        const lineHeight = fontSize * 0.4;
        const requiredHeight = lines.length * lineHeight;

        // Check if we need a new page
        if (yPosition + requiredHeight > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        pdf.text(lines, margin, yPosition);
        yPosition += requiredHeight + 3;
        return yPosition;
      };

      // Helper function to add section header
      const addSectionHeader = (title) => {
        // Check if we need a new page for the header
        if (yPosition + 15 > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        // Add background rectangle
        pdf.setFillColor(200, 200, 200); // Light gray
        pdf.rect(margin, yPosition - 2, contentWidth, 8, 'F');

        // Add title
        pdf.setFontSize(12);
        pdf.setFont('helvetica', 'bold');
        pdf.setTextColor(0, 0, 0);
        pdf.text(title, pageWidth / 2, yPosition + 3, { align: 'center' });
        yPosition += 12;
      };

      // Header
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Devansh Kumar', pageWidth / 2, yPosition, { align: 'center' });
      yPosition += 8;

      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Gurugram, Haryana, India | +91-8899190405 | devanshdixit97@gmail.com', pageWidth / 2, yPosition, { align: 'center' });
      yPosition += 15;

      // Professional Summary
      addSectionHeader('PROFESSIONAL SUMMARY');
      addText('Results-driven Senior Full Stack Developer with 5+ years of experience specializing in React Native mobile applications, React.js web development, and Node.js backend services. Proven track record of delivering scalable, high-performance applications for healthcare, e-commerce, and enterprise sectors. Expert in modern development practices including CI/CD pipelines, cloud deployment (AWS), and microservices architecture.', 9);
      addText('Skilled in integrating complex APIs (Aadhaar, OpenStreetMaps, payment gateways), implementing real-time features (WebSocket, RabbitMQ), and ensuring data security through end-to-end encryption. Strong expertise in mobile app development for both iOS and Android platforms with successful App Store and Google Play deployments.', 9);
      yPosition += 5;

      // Skills
      addSectionHeader('SKILLS');

      // Core technical skills in compact format
      addText('Programming Languages: JavaScript (ES6+), TypeScript, Python, Java, SQL, HTML5/CSS3', 8);
      addText('Frontend: React.js, React Native, Next.js, Tailwind CSS, Redux/Context API, Responsive Design', 8);
      addText('Backend & APIs: Node.js, Express.js, RESTful APIs, MongoDB, Microservices, GraphQL', 8);
      addText('Mobile Development: iOS/Android Development, Cross-platform Apps, App Store/Google Play Deployment', 8);
      addText('DevOps & Cloud: AWS (EC2, S3, RDS, Lambda), Docker, CI/CD, Git, Automated Deployment', 8);
      addText('Development Tools: VS Code, Postman, GitHub Copilot, Cursor, ChatGPT/Claude, Docker', 8);

      yPosition += 3;

      // Technical Overview
      pdf.setFillColor(240, 240, 240);
      pdf.rect(margin, yPosition, contentWidth, 15, 'F');
      addText('Technical Overview:', 9, true);
      addText('Full-stack developer with expertise in modern web and mobile technologies. Proficient in JavaScript ecosystem including React.js, React Native, and Node.js. Strong background in cloud technologies (AWS), DevOps practices, and API development. Experienced in both frontend and backend development with a focus on scalable, high-performance applications.', 8);
      yPosition += 5;

      // Work History
      addSectionHeader('WORK HISTORY');

      // SourceDOTcom Position
      pdf.setFontSize(11);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Senior Full Stack Developer', margin, yPosition);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Sep 2023 – Present', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 5;

      pdf.setFontSize(9);
      pdf.text('SourceDOTcom Pvt Ltd', margin, yPosition);
      pdf.text('Gurugram, Haryana', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 8;

      const achievements = [
        'Implemented modern development tools and workflows, resulting in 40% improvement in development speed and 60% reduction in code review time',
        'Developed comprehensive real-time messaging system with GitLab integration and AI chatbot using WebSocket, RabbitMQ, and STOMP protocols',
        'Implemented MedsKey hospital management system with live tracking, Aadhaar APIs integration, and NFC card functionality using APDU commands',
        'Established automated CI/CD pipelines for iOS and Android app deployment, reducing deployment time by 70%',
        'Developed robust RESTful APIs and GraphQL endpoints for healthcare applications, ensuring HIPAA compliance and data security',
        'Led development of 15+ mobile applications using React Native with TypeScript, achieving 4.5+ star ratings on app stores',
        'Architected scalable microservices using Node.js, Express.js, and MongoDB for enterprise-level applications',
        'Implemented advanced state management solutions using Redux and Context API for complex React applications',
        'Designed and developed responsive web applications using React.js, Next.js, and Tailwind CSS',
        'Integrated third-party APIs including payment gateways, OpenStreetMaps, and Aadhaar authentication systems',
        'Optimized application performance through code splitting, lazy loading, and bundle optimization techniques',
        'Managed AWS cloud infrastructure including EC2, S3, RDS, and Lambda functions for scalable deployments',
        'Utilized modern development tools including VS Code, Cursor, Postman, Docker, and Git for efficient development workflows',
        'Implemented security best practices including JWT authentication, data encryption, and secure API endpoints',
        'Collaborated with DevOps teams to establish automated deployment pipelines using Azure DevOps and GitHub Actions',
        'Mentored junior developers and conducted code reviews to maintain high code quality standards',
        'Implemented real-time features using WebSocket connections and message queuing systems',
        'Developed cross-platform mobile applications using React Native for both iOS and Android platforms'
      ];

      achievements.forEach(achievement => {
        // Check if we need a new page for this achievement
        if (yPosition + 8 > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        pdf.setFontSize(8);
        const lines = pdf.splitTextToSize('• ' + achievement, contentWidth - 6);
        pdf.text(lines, margin + 3, yPosition);
        yPosition += lines.length * 3.2 + 2;
      });

      yPosition += 10;

      // ICAR-IVRI Position
      pdf.setFontSize(11);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Full Stack Mobile Developer', margin, yPosition);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Dec 2020 – Sep 2023', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 5;

      pdf.setFontSize(9);
      pdf.text('ICAR - IVRI Bareilly', margin, yPosition);
      pdf.text('Uttar Pradesh', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 8;

      const icarAchievements = [
        'Developed 20+ cross-platform mobile applications using React Native for veterinary and agricultural sectors',
        'Implemented Redux and Context API for efficient state management across complex applications',
        'Built and integrated RESTful APIs using Node.js and Express.js for mobile applications',
        'Improved app performance by 50% through code optimization and lazy loading techniques',
        'Collaborated with UX/UI designers to create intuitive interfaces, achieving 90% user satisfaction',
        'Managed project timelines and deliverables while maintaining high code quality standards'
      ];

      icarAchievements.forEach(achievement => {
        // Check if we need a new page for this achievement
        if (yPosition + 8 > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        pdf.setFontSize(8);
        const lines = pdf.splitTextToSize('• ' + achievement, contentWidth - 6);
        pdf.text(lines, margin + 3, yPosition);
        yPosition += lines.length * 3.2 + 2;
      });

      yPosition += 10;

      // Krenai Services Position
      pdf.setFontSize(11);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Mobile Application Developer', margin, yPosition);
      pdf.setFont('helvetica', 'normal');
      pdf.text('Jan 2020 – Dec 2020', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 5;

      pdf.setFontSize(9);
      pdf.text('Krenai Services', margin, yPosition);
      pdf.text('Gurugram, Haryana', pageWidth - margin, yPosition, { align: 'right' });
      yPosition += 8;

      const krenaiAchievements = [
        'Created reusable React Native templates for rapid application development',
        'Integrated third-party APIs and implemented Redux for state management',
        'Built applications from scratch for both iOS and Android platforms',
        'Achieved 95% code coverage through comprehensive testing and quality checks',
        'Worked with cross-functional teams to deliver high-quality software solutions'
      ];

      krenaiAchievements.forEach(achievement => {
        // Check if we need a new page for this achievement
        if (yPosition + 8 > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        pdf.setFontSize(8);
        const lines = pdf.splitTextToSize('• ' + achievement, contentWidth - 6);
        pdf.text(lines, margin + 3, yPosition);
        yPosition += lines.length * 3.2 + 2;
      });

      yPosition += 10;

      // Notable Projects
      addSectionHeader('NOTABLE PROJECTS');

      const projects = [
        { name: 'Enterprise Messaging Platform', desc: 'Real-time communication system with intelligent chatbot integration using React.js, Node.js, WebSocket, RabbitMQ, GraphQL' },
        { name: 'Instant Messaging & My Drive App', desc: 'React Native mobile app with GitLab integration for file sharing and messaging using WebSocket and real-time messaging' },
