      projects.forEach(project => {
        // Check if we need a new page for this project
        if (yPosition + 15 > pageHeight - margin) {
          pdf.addPage();
          yPosition = margin;
        }

        pdf.setFontSize(9);
        pdf.setFont('helvetica', 'bold');
        pdf.text(project.name, margin, yPosition);
        yPosition += 4;
        pdf.setFont('helvetica', 'normal');
        pdf.setFontSize(8);
        const lines = pdf.splitTextToSize(project.desc, contentWidth);
        pdf.text(lines, margin, yPosition);
        yPosition += lines.length * 3.2 + 4;
      });

      yPosition += 10;

      // Education
      addSectionHeader('EDUCATION');
      pdf.setFontSize(9);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Bachelor of Technology', margin, yPosition);
      yPosition += 4;
      pdf.setFont('helvetica', 'normal');
      pdf.setFontSize(8);
      pdf.text('Computer Science and Engineering', margin, yPosition);
      yPosition += 3;
      pdf.text('Dronacharya College of Engineering', margin, yPosition);
      yPosition += 3;
      pdf.text('Maharshi Dayanand University, Rohtak', margin, yPosition);
      yPosition += 3;
      pdf.text('2016 - 2020 | CGPA: 7.2/10', margin, yPosition);
      yPosition += 10;

      // Certifications - Row format
      addSectionHeader('CERTIFICATIONS');

      // Check if we need a new page for certifications
      if (yPosition + 15 > pageHeight - margin) {
        pdf.addPage();
        yPosition = margin;
      }

      // Certifications in row format
      pdf.setFontSize(9);
      pdf.setFont('helvetica', 'bold');
      pdf.text('AWS Certified Developer', margin, yPosition);
      pdf.text('React Native Development', margin + 70, yPosition);
      pdf.text('Node.js Backend Development', margin + 140, yPosition);

      yPosition += 4;

      pdf.setFont('helvetica', 'normal');
      pdf.setFontSize(8);
      pdf.text('Amazon Web Services', margin, yPosition);
      pdf.text('Meta Developer Certification', margin + 70, yPosition);
      pdf.text('Udemy Certification', margin + 140, yPosition);

      yPosition += 8;

      yPosition += 5;

      // Languages
      // addSectionHeader('LANGUAGES');
      // pdf.setFontSize(8);
      // pdf.text('English (Professional) | Hindi (Native)', margin, yPosition);

      pdf.save('Devansh-Resume-5+Years.pdf');
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('An error occurred while generating the PDF. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="app-container bg-gray-100 min-h-screen">
      <div className="fixed top-4 right-4 z-50 print:hidden">
        <button
          onClick={generatePDF}
          disabled={isGenerating}
          className={`px-6 py-3 rounded-lg font-semibold text-white ${
            isGenerating ? 'bg-gray-500' : 'bg-[#2D3748] hover:bg-[#1a202c]'
          } transition-colors duration-200 shadow-lg flex items-center space-x-2`}
        >
          {isGenerating ? (
            <>
              <span className="animate-spin inline-block w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
              Generating PDF...
            </>
          ) : (
            <>
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
              </svg>
              Download Resume
